import styled, { ThemeProvider } from "styled-components";
import { createGlobalStyle } from "styled-components";
import { Link } from "react-router-dom";
import arrow from "./assets/ArrowLeft.png";
import mailImg from "./assets/Vector.png";
import passwordImg from "./assets/Lock.png";

const GlobalStyle = createGlobalStyle`
  * {
    box-sizing: border-box;
  }

  body {
    font-family: 'Noto Sans KR', sans-serif;
    background-color: ${({ theme }) => theme.backgroundColor};
    color: ${({ theme }) => theme.color};
  }
`;

const THEMES = {
  light: {
    backgroundColor: "#ffffff",
    color: "#000000",
  },
  dark: {
    backgroundColor: "#03040c",
    color: "#ffffff",
  },
};

const Container = styled.div`
  width: 400px;
  margin: auto auto;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 30px auto 80px;
  padding: 0 10px;
  font-size: 22px;
`;

const Arrow = styled.img`
  width: 24px;
`;

const Title = styled.div`
  flex-grow: 1;
  text-align: center;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  color: #585858;
  padding: 0 10px;
`;

const InputContainer = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  width: 360px;
  margin: 10px 20px 20px;
`;

const Input = styled.input`
  background-color: #f6f6f6;
  border: 1px solid #eeeeee;
  border-radius: ${({ $round }) => ($round ? `9999px` : `8px`)};
  color: #585858;
  font-size: 16px;
  padding: 16px;
  width: 360px;
  height: 50px;
  display: flex;
  margin: 10px 20px 20px;

  padding-left: 40px;
`;

const Icon = styled.span`
  position: absolute;
  left: 10px; /* 컨테이너 내에서의 위치 조정 */
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
`;

const Button = styled(Link)`
  background-color: #004a9e;
  border: none;
  border-radius: ${({ $round }) => ($round ? `9999px` : `8px`)};
  color: #ffffff;
  cursor: pointer;
  font-size: 16px;
  padding: 16px;
  width: 360px;
  height: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 20px 20px 60px;
  text-decoration: none;

  &:hover,
  &:active {
    background-color: #16457a;
  }
`;

const Question = styled.div`
  display: flex;
  justify-content: center;
  color: #000000;
  font-size: 14px;

  align-items: center;
  justify-content: center;
  position: fixed;
  left: 0;
  bottom: 0;
  width: 100%;
  padding: 60px;
`;

const Linkto = styled(Link)`
  color: #004a9e;
  text-decoration: none;
  margin-left: 5px;
`;

function JoinSpace() {
  return (
    <ThemeProvider theme={THEMES["light"]}>
      <Container>
        <GlobalStyle />
        <Header>
          <Arrow src={arrow} alt="이전" />
          <Title>가입하기</Title>
        </Header>
        <Form>
          <Label>이메일</Label>
          <Input placeholder="이메일을 입력해 주세요" />
          <InputWithIcon
            icon={<mailImg />}
            placeholder="이메일을 입력해 주세요"
          />
          <Label>비밀번호</Label>
          <Input placeholder="비밀번호를 입력해 주세요" />
          <Button>회원가입</Button>
        </Form>
        <Question>
          아직 회원이 아니신가요? <Linkto to="/JoinSpace">회원가입</Linkto>
        </Question>
      </Container>
    </ThemeProvider>
  );
}

export default JoinSpace;
