import styled, { ThemeProvider } from "styled-components";
import { createGlobalStyle } from "styled-components";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import arrow from "../assets/ArrowLeft.png";
import mailImg from "../assets/Vector.png";
import passwordImg from "../assets/Lock.png";
import Input from "./Input";
import { type } from "../../node_modules/axios/index.d";

const GlobalStyle = createGlobalStyle`
  * {
    box-sizing: border-box;
  }

  body {
    font-family: 'Noto Sans KR', sans-serif;
    background-color: ${({ theme }) => theme.backgroundColor};
    color: ${({ theme }) => theme.color};
  }
`;

const THEMES = {
  light: {
    backgroundColor: "#ffffff",
    color: "#000000",
  },
  dark: {
    backgroundColor: "#03040c",
    color: "#ffffff",
  },
};

const Container = styled.div`
  width: 400px;
  margin: auto auto;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 30px auto 80px;
  padding: 0 10px;
  font-size: 22px;
`;

const Span = styled(Link)``;

const Arrow = styled.img`
  width: 24px;
`;

const Title = styled.div`
  flex-grow: 1;
  text-align: center;
  margin-right: 15px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  color: #585858;
  padding: 0 10px;
`;

const Button = styled(Link)`
  background-color: #004a9e;
  border: none;
  border-radius: ${({ $round }) => ($round ? `9999px` : `8px`)};
  color: #ffffff;
  cursor: pointer;
  font-size: 16px;
  padding: 16px;
  width: 360px;
  height: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 20px 20px 60px;
  text-decoration: none;

  &:hover,
  &:active {
    background-color: #16457a;
  }
`;

const Question = styled.div`
  display: flex;
  justify-content: center;
  color: #000000;
  font-size: 14px;

  align-items: center;
  justify-content: center;
  position: fixed;
  left: 0;
  bottom: 0;
  width: 100%;
  padding: 60px;
`;

const Linkto = styled(Link)`
  color: #004a9e;
  text-decoration: none;
  margin-left: 5px;
`;

function JoinSpace() {
  return (
    <ThemeProvider theme={THEMES["light"]}>
      <Container>
        <GlobalStyle />
        <Header>
          <Span to="/">
            <Arrow src={arrow} alt="이전" />
          </Span>
          <Title>가입하기</Title>
        </Header>
        <Form>
          <Label>이메일</Label>
          <div>
            <Input
              icon={mailImg}
              type="text"
              placeholder="이메일을 입력해 주세요"
            />
            <label></label>
            <button>중복체크</button>
            <button>인증번호 받기</button>
          </div>
          <Label>비밀번호</Label>
          <div>
            <Input icon={passwordImg} placeholder="비밀번호를 입력해주세요" />
          </div>
          <Button>회원가입</Button>
        </Form>
        <Question>
          이미 계정이 있으신가요? <Linkto to="/LoginSpace">로그인</Linkto>
        </Question>
      </Container>
    </ThemeProvider>
  );
}

export default JoinSpace;
