import styled, { ThemeProvider } from "styled-components";
import { createGlobalStyle } from "styled-components";
import { Link } from "react-router-dom";
import axios from "axios";
import arrow from "../assets/ArrowLeft.png";
import mailImg from "../assets/Vector.png";
import passwordImg from "../assets/Lock.png";
import Input from "./Input";
import { useState } from "react";

const GlobalStyle = createGlobalStyle`
  * {
    box-sizing: border-box;
  }

  body {
    font-family: 'Noto Sans KR', sans-serif;
    background-color: ${({ theme }) => theme.backgroundColor};
    color: ${({ theme }) => theme.color};
  }
`;

const THEMES = {
  light: {
    backgroundColor: "#ffffff",
    color: "#000000",
  },
  dark: {
    backgroundColor: "#03040c",
    color: "#ffffff",
  },
};

const Container = styled.div`
  width: 400px;
  margin: auto auto;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 30px auto 80px;
  padding: 0 10px;
  font-size: 22px;
`;

const Span = styled(Link)``;

const Arrow = styled.img`
  width: 24px;
`;

const Title = styled.div`
  flex-grow: 1;
  text-align: center;
  margin-right: 15px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  color: #585858;
  padding: 0 10px;
`;

const Button = styled(Link)`
  background-color: #004a9e;
  border: none;
  border-radius: ${({ $round }) => ($round ? `9999px` : `8px`)};
  color: #ffffff;
  cursor: pointer;
  font-size: 16px;
  padding: 16px;
  width: 360px;
  height: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 20px 20px 60px;
  text-decoration: none;

  &:hover,
  &:active {
    background-color: #16457a;
  }
`;

const Question = styled.div`
  display: flex;
  justify-content: center;
  color: #000000;
  font-size: 14px;

  align-items: center;
  justify-content: center;
  position: fixed;
  left: 0;
  bottom: 0;
  width: 100%;
  padding: 60px;
`;

const Linkto = styled(Link)`
  color: #004a9e;
  text-decoration: none;
  margin-left: 5px;
`;

function JoinSpace() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [certificationNumber, setCertificationNumber] = useState("");
  const [message, setMessage] = useState("");

  const handleErrorResponse = (error) => {
    if (error.response) {
      // 서버로부터 응답은 받았으나, 에러 상태 코드를 받았을 때
      const { code, message } = error.response.data;
      switch (code) {
        case "VF":
          setMessage("유효성 검사에 실패했습니다. 입력값을 확인해주세요.");
          break;
        case "DE":
          setMessage("이미 등록된 이메일입니다.");
          break;
        case "CF":
          setMessage("인증번호 확인에 실패했습니다.");
          break;
        case "DBE":
          setMessage("데이터베이스 오류가 발생했습니다.");
          break;
        default:
          setMessage(message); // 서버에서 제공하는 기타 메시지
      }
    } else if (error.request) {
      // 요청이 이루어졌으나 응답을 받지 못했을 때
      setMessage(
        "서버로부터 응답을 받지 못했습니다. 네트워크 상태를 확인해주세요."
      );
    } else {
      // 요청이 이루어지기 전에 오류가 발생했을 때
      setMessage("요청을 보내는 중 문제가 발생했습니다.");
    }
  };

  //이메일 중복 체크
  const checkEmailDuplication = async () => {
    const fullEmail = `${email}@inu.ac.kr`;
    console.log(`Checking duplication for: ${fullEmail}`);

    try {
      const response = await axios.post("/api/v1/auth/email-check", {
        email: fullEmail,
      });

      if (response.status === 200 && response.data.code === "SU") {
        setMessage("사용 가능한 이메일입니다.");
      } else {
        setMessage(response.data.message); // Handle non-200 OK but successful response codes
      }
    } catch (error) {
      if (error.response) {
        const { code, message } = error.response.data;
        setMessage(code === "DE" ? "중복된 이메일입니다." : message);
      } else if (error.request) {
        setMessage("서버에서 응답이 없습니다.");
      } else {
        setMessage("요청을 보내는 중 오류가 발생했습니다.");
      }
    }
  };

  //인증번호 받기
  const requestCertificationNumber = async () => {
    setMessage("");

    const fullEmail = `${email}@inu.ac.kr`;
    try {
      const response = await axios.post("/api/v1/auth/email-certification", {
        email: fullEmail,
      });

      if (response.status === 200 && response.data.code === "SU") {
        // 인증번호 요청 성공
        setMessage("인증번호가 이메일로 발송되었습니다.");
      } else {
        // 서버가 성공 코드 이외의 응답을 반환한 경우
        setMessage(response.data.message);
      }
    } catch (error) {
      if (error.response) {
        // API 호출 실패 (서버 응답 오류)
        const { code, message } = error.response.data;
        if (code === "DE") {
          setMessage("이미 가입된 이메일입니다.");
        } else if (code === "VF") {
          setMessage("이메일 형식이 올바르지 않습니다.");
        } else if (code === "MF") {
          setMessage("인증 메일 발송에 실패했습니다.");
        } else if (code === "DBE") {
          setMessage("데이터베이스 오류가 발생했습니다.");
        } else {
          setMessage(message); // 기타 오류 메시지 처리
        }
      } else if (error.request) {
        // 요청이 이루어졌으나 응답을 받지 못한 경우
        setMessage("서버에서 응답이 없습니다.");
      } else {
        // 요청을 보내는 중에 오류가 발생한 경우
        setMessage("요청을 보내는 중 오류가 발생했습니다.");
      }
    }
  };

  //인증번호 확인
  const verifyCertificationNumber = async () => {
    setMessage("");

    const fullEmail = `${email}@inu.ac.kr`;
    try {
      const response = await axios.post("/api/v1/auth/check-certification", {
        email: fullEmail,
        certificationNumber: certificationNumber,
      });

      if (response.status === 200 && response.data.code === "SU") {
        // 인증번호 확인 성공
        setMessage("이메일 인증이 성공적으로 완료되었습니다.");
      } else {
        // 서버가 성공 코드 이외의 응답을 반환한 경우
        setMessage(response.data.message);
      }
    } catch (error) {
      if (error.response) {
        // API 호출 실패 (서버 응답 오류)
        const { code, message } = error.response.data;
        switch (code) {
          case "VF":
            setMessage("유효성 검사에 실패했습니다. 입력값을 확인해주세요.");
            break;
          case "CF":
            setMessage("인증번호가 올바르지 않습니다.");
            break;
          case "DBE":
            setMessage("데이터베이스 오류가 발생했습니다.");
            break;
          default:
            setMessage(message); // 기타 오류 메시지 처리
        }
      } else if (error.request) {
        // 요청이 이루어졌으나 응답을 받지 못한 경우
        setMessage("서버에서 응답이 없습니다.");
      } else {
        // 요청을 보내는 중에 오류가 발생한 경우
        setMessage("요청을 보내는 중 오류가 발생했습니다.");
      }
    }
  };

  const register = async () => {
    setMessage("");

    const fullEmail = `${email}@inu.ac.kr`;
    try {
      const response = await axios.post("/api/v1/auth/sign-up", {
        email: fullEmail,
        password: password,
        certificationNumber: certificationNumber,
      });

      if (response.status === 200 && response.data.code === "SU") {
        // 회원가입 성공
        setMessage("회원가입이 성공적으로 완료되었습니다.");
        // 여기서 추가적으로 로그인 페이지로 리다이렉트하거나,
        // 회원가입 완료 후 처리를 진행할 수 있습니다.
      } else {
        // 서버가 성공 코드 이외의 응답을 반환한 경우
        setMessage(response.data.message);
      }
    } catch (error) {
      if (error.response) {
        // API 호출 실패 (서버 응답 오류)
        const { code, message } = error.response.data;
        switch (code) {
          case "VF":
            setMessage("유효성 검사에 실패했습니다. 입력값을 확인해주세요.");
            break;
          case "DE":
            setMessage("이미 등록된 이메일입니다.");
            break;
          case "CF":
            setMessage("인증번호 확인에 실패했습니다.");
            break;
          case "DBE":
            setMessage("데이터베이스 오류가 발생했습니다.");
            break;
          default:
            setMessage(message); // 기타 오류 메시지 처리
        }
      } else if (error.request) {
        // 요청이 이루어졌으나 응답을 받지 못한 경우
        setMessage("서버에서 응답이 없습니다.");
      } else {
        // 요청을 보내는 중에 오류가 발생한 경우
        setMessage("요청을 보내는 중 오류가 발생했습니다.");
      }
    }
  };

  return (
    <ThemeProvider theme={THEMES["light"]}>
      <Container>
        <GlobalStyle />
        <Header>
          <Span to="/">
            <Arrow src={arrow} alt="이전" />
          </Span>
          <Title>가입하기</Title>
        </Header>
        <Form>
          <Label>이메일</Label>
          <div>
            <Input
              icon={mailImg}
              type="text"
              placeholder="이메일을 입력해 주세요"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <label>{message}</label>
            <button onClick={checkEmailDuplication}>중복체크</button>
            <button onClick={requestCertificationNumber}>인증번호 받기</button>
          </div>
          <div>
            <Label>인증번호</Label>
            <Input
              icon={passwordImg}
              type="text"
              placeholder="인증번호를 입력해주세요"
              value={certificationNumber}
              onChange={(e) => setCertificationNumber(e.target.value)} // 상태 업데이트
            />
            <button onClick={verifyCertificationNumber}>인증번호 확인</button>
          </div>
          <Label>비밀번호</Label>
          <div>
            <Input
              icon={passwordImg}
              type="password" // 비밀번호 숨김 처리
              placeholder="비밀번호를 입력해주세요"
              value={password}
              onChange={(e) => setPassword(e.target.value)} // 상태 업데이트
            />
          </div>
          <Button onClick={register}>회원가입</Button>
        </Form>
        <Question>
          이미 계정이 있으신가요? <Linkto to="/LoginSpace">로그인</Linkto>
        </Question>
      </Container>
    </ThemeProvider>
  );
}

export default JoinSpace;
